691ba7bf897c87e83aa9a09bf5cb79e9994cadc7.query:
UPDATE stores
SET published = true, widget_configuration = $1
WHERE store_hash = $2;

9f9a27a24eb386efa467214ef3b9aec2d96111c7.query:
UPDATE stores
SET uninstalled = true, published = false 
WHERE store_hash = $1;

af6e181247f7b17a473be8e19ef4e96699c6a894.query:
INSERT INTO stores (id, store_hash, access_token, installed_at, uninstalled) 
VALUES ($1, $2, $3, $4, false)
ON CONFLICT (store_hash) DO UPDATE set access_token = $2, installed_at = $4, uninstalled = false;
